function createTestimonialCard(testimonial) {
    const card = document.createElement('div');
    card.className = 'testimonial-card';

    const image = document.createElement('img');
    image.src = testimonial.image;
    image.alt = testimonial.name;
    image.className = 'testimonial-image';

    const content = document.createElement('div');
    content.className = 'testimonial-content';

    const text = document.createElement('p');
    text.className = 'testimonial-text';
    text.textContent = testimonial.content;

    const name = document.createElement('h3');
    name.className = 'testimonial-name';
    name.textContent = testimonial.name;

    const title = document.createElement('p');
    title.className = 'testimonial-title';
    title.textContent = testimonial.title;

    const rating = document.createElement('div');
    rating.className = 'testimonial-rating';
    rating.innerHTML = '★'.repeat(testimonial.rating) + '☆'.repeat(5 - testimonial.rating);

    const quoteLeft = document.createElement('span');
    quoteLeft.className = 'quote-left';
    quoteLeft.innerHTML = '&ldquo;';

    const quoteRight = document.createElement('span');
    quoteRight.className = 'quote-right';
    quoteRight.innerHTML = '&rdquo;';

    content.appendChild(text);
    content.appendChild(name);
    content.appendChild(title);
    content.appendChild(rating);

    card.appendChild(image);
    card.appendChild(content);
    card.appendChild(quoteLeft);
    card.appendChild(quoteRight);

    return card;
}

function renderTestimonials() {
    const container = document.getElementById('testimonial-container');
    testimonials.forEach(testimonial => {
        const card = createTestimonialCard(testimonial);
        container.appendChild(card);
    });
}

document.addEventListener('DOMContentLoaded', renderTestimonials);